package com.huobi.quantification;import org.mybatis.spring.annotation.MapperScan;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.context.annotation.ImportResource;import org.springframework.transaction.annotation.EnableTransactionManagement;@EnableTransactionManagement@SpringBootApplication@MapperScan("com.huobi.quantification.dao")@ImportResource(locations = {"classpath:spring/spring-application.xml"})public class AdminApplication {    private static final Logger logger = LoggerFactory.getLogger(AdminApplication.class);    public static void main(String[] args) {        long statTime = System.currentTimeMillis();        logger.info("开始启动服务~~~~~~~~~~~~~~~~");        SpringApplication.run(AdminApplication.class, args);        long time = System.currentTimeMillis() - statTime;        logger.info("服务启动完成,消耗时间：" + time + "毫秒");        synchronized (AdminApplication.class) {            try {                AdminApplication.class.wait();            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }}