mapper.update:
update quan_account_future_secret
set account_source_id = #{accountSourceId,jdbcType=BIGINT},
  access_key = #{accessKey,jdbcType=VARCHAR},
  secret_key = #{secretKey,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, account_source_id, access_key, secret_key
from quan_account_future_secret
where id = #{id,jdbcType=BIGINT}
