mapper.update:
update quan_depth_future_detail
set depth_future_id = #{depthFutureId,jdbcType=BIGINT},
  detail_price = #{detailPrice,jdbcType=DECIMAL},
  detail_amount = #{detailAmount,jdbcType=DOUBLE},
  date_update = #{dateUpdate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, depth_future_id, detail_price, detail_amount, date_update
from quan_depth_future_detail
where id = #{id,jdbcType=BIGINT}
