mapper.insert:
insert into strategy_hedging_config (id, coin, contract_code,
contract_type, formality_rate, slippage
)
values (#{id,jdbcType=INTEGER}, #{coin,jdbcType=VARCHAR},
#{contractCode,jdbcType=VARCHAR},
#{contractType,jdbcType=VARCHAR}, #{formalityRate,jdbcType=DECIMAL}, #{slippage,jdbcType=DECIMAL}
)

mapper.select:
select id, coin, contract_code, contract_type, formality_rate, slippage
from strategy_hedging_config
where id = #{id,jdbcType=INTEGER}

select id, coin, contract_code, contract_type, formality_rate, slippage
from strategy_hedging_config
where coin=#{coin} and contract_type=#{contractType}
