package com.huobi.quantification;import org.mybatis.spring.annotation.MapperScan;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.context.ConfigurableApplicationContext;import org.springframework.context.annotation.ImportResource;import org.springframework.transaction.annotation.EnableTransactionManagement;@EnableTransactionManagement@SpringBootApplication@MapperScan("com.huobi.quantification.dao")@ImportResource(locations= {"classpath:spring/spring-es-job.xml"})public class ServiceApplication {    private static final Logger logger = LoggerFactory.getLogger(ServiceApplication.class);    public static void main(String[] args) {        long statTime = System.currentTimeMillis();        logger.info("开始启动服务~~~~~~~~~~~~~~~~");        SpringApplication.run(ServiceApplication.class, args);        long time = System.currentTimeMillis() - statTime;        logger.info("服务启动完成,消耗时间：" + time + "毫秒");        while (true){        }    }}