package com.huobi.quantification;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.context.annotation.ImportResource;@SpringBootApplication@ImportResource(locations = {"classpath:spring/spring-application.xml"})public class StrategyApplication {    private static final Logger logger = LoggerFactory.getLogger(StrategyApplication.class);    public static void main(String[] args) {        long statTime = System.currentTimeMillis();        logger.info("开始启动服务~~~~~~~~~~~~~~~~");        SpringApplication.run(StrategyApplication.class, args);        long time = System.currentTimeMillis() - statTime;        logger.info("服务启动完成,消耗时间：" + time + "毫秒");        while (true) {        }    }}